CREATE TABLE `bz_posts` (
  `ID` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `post_author` bigint(20) unsigned NOT NULL DEFAULT '0',
  `post_date` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_date_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content` longtext COLLATE utf8mb4_unicode_ci NOT NULL,
  `post_title` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `post_excerpt` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `post_status` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'publish',
  `comment_status` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'open',
  `ping_status` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'open',
  `post_password` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `post_name` varchar(200) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `to_ping` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `pinged` text COLLATE utf8mb4_unicode_ci NOT NULL,
  `post_modified` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_modified_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content_filtered` longtext COLLATE utf8mb4_unicode_ci NOT NULL,
  `post_parent` bigint(20) unsigned NOT NULL DEFAULT '0',
  `guid` varchar(255) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `menu_order` int(11) NOT NULL DEFAULT '0',
  `post_type` varchar(20) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT 'post',
  `post_mime_type` varchar(100) COLLATE utf8mb4_unicode_ci NOT NULL DEFAULT '',
  `comment_count` bigint(20) NOT NULL DEFAULT '0',
  PRIMARY KEY (`ID`),
  KEY `post_name` (`post_name`(191)),
  KEY `type_status_date` (`post_type`,`post_status`,`post_date`,`ID`),
  KEY `post_parent` (`post_parent`),
  KEY `post_author` (`post_author`)
) ENGINE=InnoDB AUTO_INCREMENT=476 DEFAULT CHARSET=utf8mb4 COLLATE=utf8mb4_unicode_ci;


INSERT INTO bz_posts VALUES
("1","1","2019-03-01 04:36:21","2019-03-01 04:36:21","<em>Những chiếc sơ mi voan mùa hè không chỉ có nhiều kiểu dáng cách điệu mà cũng rất dễ để có thể set đồ đẹp cho thời trang công sở hè.</em>\n\nSơ mi voan là gu phổ biến của bạn gái công sở những ngày hè nắng nóng, những kiểu sơ mi được thiết kế cách điệu với chất liệu voan mềm, gam màu dịu nhẹ hay những sắc hoạ tiết bắt mắt nổi bật cho người hè.\n\nVới một chiếc sơ mi voan bạn gái có thể dễ dàng mix cùng chân váy hay quần tây hoặc jean, mỗi kiểu mix sẽ cho 1 phong cách khác nhau chắc chắn cô nàng nào cũng sẽ mê. Để Tbiz gợi ý cho bạn mix đồ cùng những mẫu sơ mi voan Hàn Quốc cực đẹp hè 2017 này nhé!\n<div id=\"attachment_41325\" class=\"wp-caption alignnone\"><a href=\"https://thoitrangtre.biz/wp-content/uploads/2015/04/so-mi-voan-1.jpg\"><img class=\"wp-image-41325 size-full\" src=\"https://thoitrangtre.biz/wp-content/uploads/2015/04/so-mi-voan-1.jpg\" sizes=\"(max-width: 749px) 100vw, 749px\" srcset=\"https://thoitrangtre.biz/wp-content/uploads/2015/04/so-mi-voan-1.jpg 749w, https://thoitrangtre.biz/wp-content/uploads/2015/04/so-mi-voan-1-220x350.jpg 220w\" alt=\"so mi voan (1)\" width=\"749\" height=\"1191\" /></a>\n<p class=\"wp-caption-text\">chiếc sơ mi voan trắng kiểu thắt nơ duyên dáng, thanh lịch với thời trang công sở</p>\n\n</div>\n&nbsp;\n<div id=\"attachment_41326\" class=\"wp-caption alignnone\"><a href=\"https://thoitrangtre.biz/wp-content/uploads/2015/04/so-mi-voan-2.jpg\"><img class=\"wp-image-41326 size-full\" src=\"https://thoitrangtre.biz/wp-content/uploads/2015/04/so-mi-voan-2.jpg\" sizes=\"(max-width: 753px) 100vw, 753px\" srcset=\"https://thoitrangtre.biz/wp-content/uploads/2015/04/so-mi-voan-2.jpg 753w, https://thoitrangtre.biz/wp-content/uploads/2015/04/so-mi-voan-2-305x343.jpg 305w\" alt=\"so mi voan (2)\" width=\"753\" height=\"848\" /></a>\n<p class=\"wp-caption-text\">với kiểu sơ mi trắng này sẽ nổi bật với chân váy màu hoặc hoạ tiết, như kiểu hoạ tiết lạ mắt này bán gái cũng sẽ nổi bật hơn nhé</p>\n\n</div>\n&nbsp;\n\n<a href=\"https://thoitrangtre.biz/wp-content/uploads/2015/04/so-mi-voan-3.jpg\"><img class=\"alignnone size-full wp-image-41327\" src=\"https://thoitrangtre.biz/wp-content/uploads/2015/04/so-mi-voan-3.jpg\" sizes=\"(max-width: 750px) 100vw, 750px\" srcset=\"https://thoitrangtre.biz/wp-content/uploads/2015/04/so-mi-voan-3.jpg 748w, https://thoitrangtre.biz/wp-content/uploads/2015/04/so-mi-voan-3-218x350.jpg 218w\" alt=\"so mi voan (3)\" width=\"750\" height=\"1204\" /></a>\n\n<a href=\"https://thoitrangtre.biz/wp-content/uploads/2015/04/so-mi-voan-4.jpg\"><img class=\"alignnone size-full wp-image-41328\" src=\"https://thoitrangtre.biz/wp-content/uploads/2015/04/so-mi-voan-4.jpg\" sizes=\"(max-width: 752px) 100vw, 752px\" srcset=\"https://thoitrangtre.biz/wp-content/uploads/2015/04/so-mi-voan-4.jpg 752w, https://thoitrangtre.biz/wp-content/uploads/2015/04/so-mi-voan-4-305x323.jpg 305w\" alt=\"so mi voan (4)\" width=\"752\" height=\"797\" /></a>\n\nvới những mẫu sơ mi mà Tbiz giới thiệu đến các bạn gái công sở được tuyển chọn từ các hãng thời trang nổi tiếng của Hàn Quốc, không chỉ những sản phẩm mới được thiết kế theo mùa từng năm mà được cập nhật phù hợp với xu hướng thời trang trẻ của những cô nàng trẻ sành thời trang","Cách phối đồ mùa hè với voan cực xịn cho dân công sở","","publish","open","open","","chao-moi-nguoi","","","2019-03-01 15:41:55","2019-03-01 08:41:55","","0","https://tppone.com/demo/fashion5/?p=1","0","post","","1"),
