CREATE TABLE `bz_posts` (
  `ID` bigint(20) unsigned NOT NULL AUTO_INCREMENT,
  `post_author` bigint(20) unsigned NOT NULL DEFAULT '0',
  `post_date` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_date_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content` longtext NOT NULL,
  `post_title` text NOT NULL,
  `post_excerpt` text NOT NULL,
  `post_status` varchar(20) NOT NULL DEFAULT 'publish',
  `comment_status` varchar(20) NOT NULL DEFAULT 'open',
  `ping_status` varchar(20) NOT NULL DEFAULT 'open',
  `post_password` varchar(255) NOT NULL DEFAULT '',
  `post_name` varchar(200) NOT NULL DEFAULT '',
  `to_ping` text NOT NULL,
  `pinged` text NOT NULL,
  `post_modified` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_modified_gmt` datetime NOT NULL DEFAULT '0000-00-00 00:00:00',
  `post_content_filtered` longtext NOT NULL,
  `post_parent` bigint(20) unsigned NOT NULL DEFAULT '0',
  `guid` varchar(255) NOT NULL DEFAULT '',
  `menu_order` int(11) NOT NULL DEFAULT '0',
  `post_type` varchar(20) NOT NULL DEFAULT 'post',
  `post_mime_type` varchar(100) NOT NULL DEFAULT '',
  `comment_count` bigint(20) NOT NULL DEFAULT '0',
  PRIMARY KEY (`ID`),
  KEY `post_name` (`post_name`(191)),
  KEY `type_status_date` (`post_type`,`post_status`,`post_date`,`ID`),
  KEY `post_parent` (`post_parent`),
  KEY `post_author` (`post_author`)
) ENGINE=InnoDB AUTO_INCREMENT=940 DEFAULT CHARSET=utf8;


INSERT INTO bz_posts VALUES
("1","1","2017-09-14 04:05:14","2017-09-14 04:05:14","Ở bài trước, mình đã phân tích: Ohui The First có tốt không ?? Bài viết này sẽ giới thiệu những sản phẩm nằm trong dòng mỹ phẩm Ohui The First Renewal. Khi đã có sản phẩm trong tay, chúng ta hãy cùng nhau tìm hiểu cách sử dụng bộ sản phẩm toàn diện này nhé.\n\n<b>Làm sạch da mặt:</b> Làm sạch da mặt là bước đầu tiên và cũng quan trọng nhất trong bất kỳ liệu trình chăm sóc da nào. Trước khi sử dụng bộ <b>my pham Ohui The First</b>, hãy làm sạch da mặt một cách hiệu quả với sản phẩm <b>Ohui Cell revolution massage foam</b>. Đây là sữa rửa mặt dạng bọt,vừa làm sữa rửa mặt vừa có thể dùng để massage nhẹ nhàng.\n\n<b>Cách sử dụng: </b>\n<ul>\n 	<li class=\"bullet-star\">Bước 1: Lấy 1 lượng sản phẩm bằng trái bóng bàn</li>\n 	<li class=\"bullet-star\">Bước 2: Massage từng phần trán, mũi, 2 má, cằm. Massage toàn mặt trong vòng 30 giây tới 1 phút.</li>\n 	<li class=\"bullet-star\">Bước 3: Rửa lại nhiều lần bằng nước sạch</li>\n</ul>","Hướng dẫn sử dụng bộ mỹ phẩm Ohui The First","","publish","open","open","","chao-moi-nguoi","","","2017-09-14 21:41:34","2017-09-14 14:41:34","","0","https://bizhostvn.com/w/hatdieu/?p=1","0","post","","1"),
