ottom: 5px;\n    display: -webkit-box;\n    position: absolute;\n    z-index: 9999;\n    background: rgba(0,0,0,0.6);\n    margin-left: 10.5%;\n    height: 30px;\n    color: white;\n    padding-left: 10px;\n    padding-right: 10px;\n    padding-top: 3px;\n    padding-bottom: 10px;\n    border-bottom-right-radius: 11px;}\n.breadcrumb ul li{list-style: none; float: left; margin: 0; padding-right: 8px}\n.col{    padding: 0 6px 10px;}\n.du-an .col{padding-left:2px; padding-right: 2px}\n.nav-dropdown.dropdown-uppercase>li>a{font-weight:bold}\n .row .row:not(.row-collapse){margin-left:0px; margin-right:0px}\n.tab-du-an ul{margin: 0 auto;\n    background: #f3f3f3;\n    padding: 5px; height: 35px}\n.tab-du-an ul li{list-style: none;\n    float: left;\n    margin-right: 10px;}\n.tab-du-an{text-align: center}\n.tab-du-an ul li a{font-size: 14px; color: black; font-weight: bold}\n.tab-du-an ul li a:hover{border-bottom: 1px solid black}\n\n#main .flex-right{margin-top:0px}","qshome","","inherit","closed","closed","","30-revision-v1","","","2017-08-22 17:09:13","2017-08-22 10:09:13","","30","https://bizhostvn.com/w/kientruc/30-revision-v1/","0","revision","","0"),
